<style style="text/css">
				
.scroll-right {
 height: 40px;	
 overflow: hidden;
 position: relative;
/*	width:90%;*/
	
background: goldenrod;
}
.scroll-right .inner1 {
 position: absolute;
 width: 100%;
 height: 100%;
 /*margin: 0;*/
	background: goldenrod;
 line-height: 50px;
 text-align: center;
 /* Starting position */
 -moz-transform:translateX(100%);
 -webkit-transform:translateX(100%);	
 transform:translateX(100%);
 /* Apply animation to this element */	
 -moz-animation: scroll-right 8s linear infinite;
 -webkit-animation: scroll-right 8s linear infinite;
 animation: scroll-right 8s linear infinite;
}
.scroll-right .inner2 {
 position: absolute;
 width: 100%;
 height: 100%;
 margin: 0;
	background: goldenrod;
 line-height: 50px;
 text-align: center;
 /* Starting position */
 -moz-transform:translateX(100%);
 -webkit-transform:translateX(100%);	
 transform:translateX(100%);
 /* Apply animation to this element */	
 -moz-animation: scroll-right 20s linear infinite;
 -webkit-animation: scroll-right 20s linear infinite;
 animation: scroll-right 20s linear infinite;
}
/* Move it (define the animation) */
@-moz-keyframes scroll-right {
 0%   { -moz-transform: translateX(-100%); }
 100% { -moz-transform: translateX(100%); }
}
@-webkit-keyframes scroll-right {
 0%   { -webkit-transform: translateX(-100%); }
 100% { -webkit-transform: translateX(100%); }
}
@keyframes scroll-right {
 0%   { 
 -moz-transform: translateX(-100%); /* Browser bug fix */
 -webkit-transform: translateX(-100%); /* Browser bug fix */
 transform: translateX(-100%); 		
 }
 100% { 
 -moz-transform: translateX(100%); /* Browser bug fix */
 -webkit-transform: translateX(100%); /* Browser bug fix */
 transform: translateX(100%); 
 }
}
</style>
